import React, { Component } from 'react'; 
// import { BrowserRouter, NavLink, Redirect } from 'react-router-dom';
// import Route from 'react-router-dom/Route';
import ColorButton from './ColorButton.js';

import bug from './open-iconic/svg/bug.svg';

import './App.css';



class App extends Component {
  constructor(){
    super();
    this.state = {
      colorButtons: [
        {id: 1, bText: "Reddy", color: "danger"},
        {id: 2, bText: "Bluey", color: "primary"},
        {id: 3, bText: "Greeny", color: "success"},
        {id: 4, bText: "Grey", color: "secondary"}
      ],
      title: "Color the Page"
    }
  };

  handleColorPage = (e) => {
    document.getElementsByTagName('body')[0].classList.remove("bg-danger", "bg-primary", "bg-success", "bg-secondary");
    document.getElementsByTagName('body')[0].classList.add("bg-" +e.target.dataset.color);
  };

  ColorButtonC = (v,i) => <ColorButton key={v.id} colorData={v.color} btnClasses={"btn btn-" +v.color+ " m-1"} doColorPage={ this.handleColorPage } bText={v.bText} />;

  render() {
    return (

      <div className="App mt-5">     
        <h1 className="display-3 m-4">{ this.state.title }</h1>
        { this.state.colorButtons.map(this.ColorButtonC) }
        <button  className="btn"><img src={ bug } /></button>
      </div>
    );
  }
}

export default App;
