import React, { Component } from 'react'; 
import { BrowserRouter, NavLink, Redirect } from 'react-router-dom';
import Route from 'react-router-dom/Route';

import './App.css';

import Card from './Card';
import NewCard from './NewCard';


const Abuja = () => {
  return (<h1>barcelona my capitol  </h1>);
}

class App extends Component {
  constructor(){
    super();
    this.state = {
      cards: [
        {id: 1, title: "Azusion"},
        {id: 2, title: "London"},
        {id: 3, title: "Dubai City"}
      ],
      city: "Lagos",
      loggedIn: false
    }
  };

  pay = () => "FG will pay now!";

  addCard = t => {
    let card_ = this.state.cards; card_.push({id: card_.length + 1, title: t});
    this.setState({cards: card_});
  }; 

  showCards = (i,k,arr) => {
    return (<Card pay={ this.pay.bind(this) } key={ i.id } remove={ v => { arr.splice(k,1);
    this.setState({ cards: arr }) } } save={ v => {arr[k].title = v;
    this.setState({ cards: arr }) } } >{ i.title }</Card>)
  };

  login = () => this.setState({loggedIn: true}, () => console.log("loggedIn"));



  render() {
    return (

<BrowserRouter>

      <div className="App">



<nav className="navbar navbar-expand-lg navbar-light bg-light">
  <a className="navbar-brand" href="#">Navbar</a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span className="navbar-toggler-icon"></span>
  </button>

  <div className="collapse navbar-collapse" id="navbarSupportedContent">
    <ul className="navbar-nav mr-auto">
      <li className="nav-item active">
        <NavLink to="/" exact activeStyle={ {color:"red" } }  className="nav-link">Home<span className="sr-only">(current)</span></NavLink> 
      </li>
      <li className="nav-item">
        <NavLink to="/lagos/jojo" activeStyle={ {color:"red" } }  className="nav-link">Lagos</NavLink>        
      </li>
      <li className="nav-item">
        <NavLink to="/abuja" activeStyle={ {color:"red"} } className="nav-link">Abuja</NavLink>        
      </li>
      <li className="nav-item dropdown">
        <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Dropdown
        </a>
        <div className="dropdown-menu" aria-labelledby="navbarDropdown">
          <a className="dropdown-item" href="#">Action</a>
          <a className="dropdown-item" href="#">Another action</a>
          <div className="dropdown-divider"></div>
          <a className="dropdown-item" href="#">Something else here</a>
        </div>
      </li>
      <li className="nav-item">
        <a className="nav-link disabled" href="#">Disabled</a>
      </li>
    </ul>
    <form className="form-inline my-2 my-lg-0">
      <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
      <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
    </form>
  </div>
</nav>


        <button onClick={ () => this.setState({city: "London"}) }>City</button>
        <header className="">

          <div className="text-center col-md-4">
            {
              this.state.cards.map(this.showCards)
            }
            <NewCard addCard={ this.addCard.bind(this) } />
          </div>
        </header>

        <Route exact path="/lagos/:user" render={ ({match}) => {return (<h1>hey from {this.state.city} route {match.params.user.toUpperCase()}</h1>)} } />

        <Route path="/abuja" component={Abuja} />

        <button onClick={ this.login.bind(this) } className="btn btn-warning">Log In</button>

      </div>

</BrowserRouter>

    );
  }
}

export default App;
