import React, { Component } from 'react';

class Card extends React.Component {

	view = () => {
		return(
	      <div className="card m-1 p-2">
	        <p>
	        	<input value={ this.props.children } type="text" className="p-1" />
	        </p>
	        <div>

	        	<button className="btn btn-success col-6">Save</button>
	        </div>
	      </div>
	    )
	};

	edit = () => {
		return(
	      <div className="card m-1 p-2">
	        <h3>{ this.props.children }</h3>
	        <div>

	        	<button className="btn btn-warning col-6">Edit</button>
	        	<button className="btn btn-danger col-6">Removee</button>
	        </div>
	      </div>
	    )
	}

  render(){
    return this.view();
  }
}

export default Card;
