import React, { Component } from 'react';

class Card extends React.Component {

	state = {
		mode: false
	}

	mode = () => {
		this.setState({
			mode: !this.state.mode
		});
	};
	save = (e) => {
		console.log(this.refs.cardText);
		e.preventDefault();
		this.mode();
	};



	edit = () => {
		return(
	      <div className="card m-1 p-2">
	        <p>
	        	<input ref="cardText" defaultValue={ this.props.children } type="text" className="w-100 p-1" />
	        </p>
	        <div>

	        	<button className="btn btn-success col-6" onClick={this.save} >Save</button>
	        	<button className="btn btn-secondary col-6" onClick={this.mode} >Cancel</button>
	        </div>
	      </div>
	    )
	};

	view = () => {
		return(
	      <div className="card m-1 p-2">
	        <h3>{ this.props.children }</h3>
	        <div>

	        	<button className="btn btn-warning col-6" onClick={ this.mode }>Edit</button>
	        	<button className="btn btn-danger col-6" onClick={ console.log(this.props.bacon) } >Remove</button>
	        </div>
	      </div>
	    )
	}

  render(){
    return (this.state.mode)? this.edit(): this.view();
  }
}

export default Card;
