import React, { Component } from 'react'; 
// import { BrowserRouter, NavLink, Redirect } from 'react-router-dom';
// import Route from 'react-router-dom/Route';
import ColorButton from './ColorButton.js';

import './App.css';



class App extends Component {
  constructor(){
    super();
    this.state = {
      colorButtons: [
        {id: 1, bText: "Reddy", color: "danger"},
        {id: 2, bText: "Bluey", color: "primary"},
        {id: 3, bText: "Greeny", color: "success"},
        {id: 4, bText: "Grey", color: "secondary"}
      ],
      title: "Color the Page"
    }
  };
  bodyEl = document.getElementsByTagName('body')[0];

  handleColorPage = (e) => {
    this.bodyEl.classList.add("bg-" +e.target.dataset.color);
  };

  clearBg = (e) => {
    this.bodyEl.setAttribute('style', '');
  };

  wrapped = n => n>255 ? n-255 : n;

  addRandomColor = () => {
    this.clearBg();
    let initialColor = [Math.round(Math.random() *255), Math.round(Math.random() *255), Math.round(Math.random() *255)];

    this.bodyEl.style.backgroundImage = "conic-gradient(rgb("+initialColor[0]+","+initialColor[1]+","+initialColor[2]+"), rgb(" +this.wrapped(initialColor[0]+168)+ ","+this.wrapped(initialColor[1]+36)+","+this.wrapped(initialColor[2]+198)+"))"
    // this.bodyEl.style.backgroundColor = "rgb(" +Math.round(Math.random() *255)+ " " +Math.round(Math.random() *255)+ " " +Math.round(Math.random() *255)+ ")";
  };

  randomizeColorStops = (firstStop,secondStop) => {
    this.clearBg();
    let initialColor = [Math.round(Math.random() *255), Math.round(Math.random() *255), Math.round(Math.random() *255)];

    this.bodyEl.style.backgroundImage = "conic-gradient(rgb("+initialColor[0]+","+initialColor[1]+","+initialColor[2]+")" +firstStop+ "%, rgb(" +this.wrapped(initialColor[0]+168)+ ","+this.wrapped(initialColor[1]+36)+","+this.wrapped(initialColor[2]+198)+")" +secondStop+ "%)"
  };

  animateColor = () => {
    let firstStop = 1;
    let secondStop = 100
    setInterval(() => {
      // Process stops
      // TODO ....

      // Call randomize with stops
      this.randomizeColorStops(firstStop++, secondStop);
      firstStop++;
    }, 100);    
  }

  render() {
    return (

      <div className="App mt-5">     
        <h1 className="display-3 m-4 title">{ this.state.title }</h1>
        <button className="btn random" onClick={ this.addRandomColor }>Random</button>
        <button className="btn random" onClick={ this.animateColorx }>Animate</button>
        <button ref="someRef" className="btn"><span className="text-muted oi oi-ban" title="ban" aria-hidden="true" onClick={ this.clearBg }></span></button>

      </div>
    );
  }
}

export default App;
