import React, { Component } from 'react';

class Card extends React.Component {

	edit = () => {
		return(
      <div className="card m-1 p-2">
        <h3>{ this.props.children }</h3>
        <div>

        	<button className="btn btn-warning col-6">Edit</button>
        	<button className="btn btn-danger col-6">Remove</button>
        </div>
      </div>
    )
	}

  render(){
    return edit();
  }
}

export default Card;
