import React, { Component } from 'react';

class NewCard extends React.Component {
	constructor(){
		super();
		this.state = {
			add: true
		}
	}
	runAddCard = (e) => {
		if (this.refs.newCardText.value !== "") {
			this.props.addCard(this.refs.newCardText.value);
		}
		this.adder();
		e.preventDefault();
		// cancel editing...

	}

	showForm = () => {
		return(
			<form className="">
				<input ref="newCardText" autofocus type="text" className="card m-1 p-2" />
				<button className="btn btn-success col-6" onClick={ this.runAddCard } >Add</button>
			</form>
		);
	};
	hideForm = () => {
		return(
			<button className="btn btn-primary col-6" onClick={ this.adder } >Add</button>
		);
	};
	adder = () => this.setState({ add: !this.state.add });

	render(){
		return (this.state.add)? this.hideForm() : this.showForm();
	}
}

export default NewCard;
