import React, { Component } from 'react'; 
// import { BrowserRouter, NavLink, Redirect } from 'react-router-dom';
import Route from 'react-router-dom/Route';

import './App.css';



class App extends Component {
  constructor(){
    super();
    this.state = {
      colorButtons: [
        {id: 1, bText: "Reddy", color: "danger"},
        {id: 2, bText: "Bluey", color: "primary"},
        {id: 3, bText: "Greeny", color: "success"},
        {id: 4, bText: "Grey", color: "secondary"}
      ]
    }
  };

  handleColorPage = (e) => console.log(e.target.textContent);

  // document.getElementsByTagName('body')[0].classList.add({});


  render() {
    return (

      <div className="App mt-5">
       { this.state.colorButtons.map( (v,i) => <button key={v.id} data-color={v.color} className={"btn btn-" +v.color+ " m-1"} onClick={ this.handleColorPage }>{v.bText}</button> ) }
      </div>
    );
  }
}

export default App;
