import React, { Component } from 'react';

class NewCard extends React.Component {
	constructor(){
		super();
		this.state = {
			add: false
		}
	}
	showForm = () => {
		return(
			<form className="">
				<input type="text" className="card m-1 p-2" />
				<button className="btn btn-primary col-6">Add</button>
			</form>
		);
	}
	hideForm = () => {
		return(
			<button className="btn btn-primary col-6" onClick={ () => this.setState({ add: !add }) } >Add</button>
		);
	}
	render(){
		return (this.state.add)? this.hideForm() : this.showForm();
	}
}

export default NewCard;
