import React, { Component } from 'react'; 
import { BrowserRouter } from 'react-router-dom';
import Route from 'react-router-dom/Route';

import './App.css';

import Card from './Card';
import NewCard from './NewCard';

class App extends Component {
  constructor(){
    super();
    this.state = {
      cards: [
        {id: 1, title: "Azusion"},
        {id: 2, title: "London"},
        {id: 3, title: "Dubai City"}
      ]
    }
  };

  addCard = t => {
    let card_ = this.state.cards; card_.push({id: card_.length + 1, title: t});
    this.setState({cards: card_});
  } 

  showCards = (i,k,arr) => {
    return (<Card key={ i.id } remove={ v => { arr.splice(k,1);
    this.setState({ cards: arr }) } } save={ v => {arr[k].title = v;
    this.setState({ cards: arr }) } } >{ i.title }</Card>)
  }

  render() {
    return (

<BrowserRouter>

      <div className="App">
        <header className="">

          <div className="text-center w-25">
            {
              this.state.cards.map(this.showCards)
            }
            <NewCard addCard={ this.addCard.bind(this) } />
          </div>
        </header>

        <Route exact path="/lagos" render={ () => {return(<div>hey from lagos route</div>)} } />
        <Route exact path="/abuja" render={ () => {return (<h1>Abuja my capitol</h1>)} } />

      </div>

</BrowserRouter>

    );
  }
}

export default App;
