import React, { Component } from 'react'; 
// import { BrowserRouter, NavLink, Redirect } from 'react-router-dom';
import Route from 'react-router-dom/Route';

import './App.css';



class App extends Component {
  constructor(){
    super();
    this.state = {
      cards: [
        {id: 1, title: "Azusion"},
        {id: 2, title: "London"},
        {id: 3, title: "Dubai City"}
      ]
    }
  };

  handleColorPage = () => document.getElementByTagName('body').style.backgroundColor = 'red';


  render() {
    return (

      <div className="App mt-5">

        <button className="btn btn-danger m-1" onClick={ handleColorPage }>reddy</button>
        <button className="btn btn-primary m-1">bluey</button>
        <button className="btn btn-success m-1">greeny</button>
        <button className="btn btn-dark m-1">blacky</button>

      </div>
    );
  }
}

export default App;
